mapper.select:
SELECT COUNT(*) FROM admin


		id = #{id}


		AND EMAIL = #{email}


		AND telephone = #{telephone}

SELECT id, organization_name, address, name, telephone, email, request_time, approve_time, status FROM festivalolle.v_admin WHERE status != 0 AND status != 3 AND status != 4
AND id = #{id}
ORDER BY 7 DESC

mapper.update:
UPDATE admin
SET status = 2, approve_time = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
WHERE id= #{id}

mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM v_admin WHERE ( (status != 0 AND status != 3 AND status != 4)
AND (id LIKE '%'|| #{keyword} || '%' OR name LIKE '%'|| #{keyword} || '%' OR organization_name LIKE '%'|| #{keyword} || '%' OR telephone LIKE '%'|| #{keyword} || '%')
) ORDER BY 8 DESC) A) WHERE RN BETWEEN #{start} AND #{end}
