mapper.select:

SELECT result.* 
FROM (SELECT festival_code, title,
             state_name, city_name, image, 
             rating
      FROM festivalolle.v_festival_main WHERE status = 1
      ORDER BY festival_code DESC
     ) result



	festival_code < #{lastFestivalCode}

AND ROWNUM <= #{perPage}


SELECT * FROM festivalolle.v_festival
WHERE status != 0
AND festival_code = #{festivalCode}

SELECT festival_code, title, state_name, city_name, image, rating FROM (SELECT ROWNUM AS rnum, fes.* FROM (SELECT festival_code, title, state_name, city_name, image, rating FROM festivalolle.v_festival_main WHERE status != 0 AND(start_date < #{periodEnd}) AND(end_date > #{periodStart})
AND state_name = #{location}
ORDER BY end_date) fes )
rnum > (SELECT rn FROM (SELECT ROWNUM AS rn, festival_code FROM (SELECT festival_code FROM festivalolle.v_festival_main WHERE status != 0 AND(start_date < #{periodEnd}) AND(end_date > #{periodStart})
AND state_name = #{location}
ORDER BY end_date) f ) WHERE festival_code = #{lastFestivalCode})
AND ROWNUM <= #{perPage}

SELECT festival_code, title, state_name, city_name, image, rating FROM (SELECT bookmark_code, fes.festival_code, title, state_name, city_name, image, rating FROM festivalolle.v_festival_main fes JOIN (SELECT bookmark_code, festival_code FROM festivalolle.bookmark WHERE member_code = #{memberCode}) bm ON fes.festival_code = bm.festival_code WHERE status != 0 ORDER BY bookmark_code DESC) result
bookmark_code < #{lastBookmarkCode}
AND ROWNUM <= #{perPage}

SELECT festival_code, title, state_name, city_name, image, rating FROM (SELECT ROWNUM rnum, fes.* FROM (SELECT festival_code, title, state_name, city_name, image, rating FROM festivalolle.v_festival_main WHERE status = 1 AND visitors != 0 AND(start_date < #{periodEnd}) AND(end_date > #{periodStart}) ORDER BY visitors DESC) fes )
rnum > (SELECT rn FROM (SELECT ROWNUM rn, festival_code FROM (SELECT festival_code FROM festivalolle.v_festival_main WHERE status = 1 AND visitors != 0 AND(start_date < #{periodEnd}) AND(end_date > #{periodStart}) ORDER BY visitors DESC) f) WHERE festival_code = #{lastFestivalCode})
AND ROWNUM <= #{perPage}
